import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const PremiumDonorDetails = () => {
  const { id } = useParams();
  const [donorDetails, setDonorDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDonorDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/donors/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch donor details');
        }
        const data = await response.json();
        setDonorDetails(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    // Fetch donor details based on the ID from your API or server
    fetchDonorDetails();

    // Cleanup function to cancel the fetch if the component unmounts
    return () => {
      // AbortController helps in cancelling the fetch request
      const controller = new AbortController();
      controller.abort();
    };
  }, [id]); // Dependency on 'id' ensures useEffect runs when the ID changes

  if (loading) {
    return <p>Loading...</p>; // Add a loading indicator or handle loading state
  }

  if (error) {
    return <p>Error: {error}</p>; // Handle error state
  }

  if (!donorDetails) {
    return <p>No donor details found</p>;
  }

  return (
    <div>
      <h2>Donor Details</h2>
      <p>ID: {donorDetails.premium_donor_id}</p>
      <p>First Name: {donorDetails.first_name}</p>
      <p>Last Name: {donorDetails.last_name}</p>
      <p>Email: {donorDetails.email}</p>
      {/* Add more details based on your premium donor data */}
    </div>
  );
};

export default PremiumDonorDetails;
