import React, { useState } from 'react';
import axios from 'axios';

const BloodTestControllerComponent = () => {
  const [donorInfo, setDonorInfo] = useState({
    donor_id: '', // You may need to update this based on your server-side schema
    name: '',
    general_health: 'good',
    disqualifying_medications: false,
    recent_travel: false,
    recent_tattoos: false,
    recent_sexual_activity: false,
    drug_use: false,
  });

  const [result, setResult] = useState(null);

  const handleCheckAndStoreBloodStatus = async () => {
    try {
      const response = await axios.post('http://localhost:5000/blood-testing/check-and-store', {
        donorInfo,
      });

      setResult(response.data);
    } catch (error) {
      console.error('Error checking and storing blood status:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const inputValue = type === 'checkbox' ? checked : value;

    setDonorInfo((prevDonorInfo) => ({
      ...prevDonorInfo,
      [name]: inputValue,
    }));
  };

  return (
    <div>
      <h2>Blood Test Controller</h2>
      <form>
        {/* ... Input fields similar to the BloodTestForm component */}
        <button type="button" onClick={handleCheckAndStoreBloodStatus}>
          Check and Store Blood Status
        </button>
      </form>

      {result && (
        <div>
          <h3>Result:</h3>
          {result.message && <p>{result.message}</p>}
          {result.bloodStatus && <p>Blood Status: {result.bloodStatus}</p>}
          {result.donorDetails && (
            <div>
              <h4>Donor Details:</h4>
              {result.donorDetails && (
  <div>
    <h4>Donor Details:</h4>
    <p>Donor ID: {result.donorDetails.donor_id}</p>
    <p>Name: {result.donorDetails.name}</p>
    <p>General Health: {result.donorDetails.general_health}</p>
    <p>Disqualifying Medications: {result.donorDetails.disqualifying_medications ? 'Yes' : 'No'}</p>
    <p>Recent Travel: {result.donorDetails.recent_travel ? 'Yes' : 'No'}</p>
    <p>Recent Tattoos: {result.donorDetails.recent_tattoos ? 'Yes' : 'No'}</p>
    <p>Recent Sexual Activity: {result.donorDetails.recent_sexual_activity ? 'Yes' : 'No'}</p>
    <p>Drug Use: {result.donorDetails.drug_use ? 'Yes' : 'No'}</p>
    {/* Add more donor details as needed */}
  </div>
)}

            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default BloodTestControllerComponent;
