import React, { useState } from 'react';
import axios from 'axios';

const BloodStockForm = () => {
  const [formData, setFormData] = useState({
    blood_group: '',
    total_unit: '',
    current_stock: '',
    blood_status: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/login/stf/inv/insert', formData);
      console.log('Blood stock inserted successfully:', response.data);
      // Handle success, e.g., show a success message to the user
    } catch (error) {
      console.error('Error during blood stock insertion:', error);
      // Handle error, e.g., show an error message to the user
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-4 bg-gray-200 rounded-md">
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="blood_group" className="block text-sm font-medium text-gray-600">
            Blood Group
          </label>
          <input
            type="text"
            id="blood_group"
            name="blood_group"
            value={formData.blood_group}
            onChange={handleChange}
            className="input-field"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="total_unit" className="block text-sm font-medium text-gray-600">
            Total Unit
          </label>
          <input
            type="text"
            id="total_unit"
            name="total_unit"
            value={formData.total_unit}
            onChange={handleChange}
            className="input-field"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="current_stock" className="block text-sm font-medium text-gray-600">
            Current Stock
          </label>
          <input
            type="text"
            id="current_stock"
            name="current_stock"
            value={formData.current_stock}
            onChange={handleChange}
            className="input-field"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="blood_status" className="block text-sm font-medium text-gray-600">
            Blood Status
          </label>
          <input
            type="text"
            id="blood_status"
            name="blood_status"
            value={formData.blood_status}
            onChange={handleChange}
            className="input-field"
            required
          />
        </div>

        <button type="submit" className="submit-button">
          Submit
        </button>
      </form>
    </div>
  );
};

export default BloodStockForm;
