import React, { createContext, useReducer, useContext, useEffect } from 'react';
import jwtDecode from 'jwt-decode';


const UserContext = createContext();

const initialState = {
  isLoggedIn: false,
  userRole: null,
  username: null,
  userData: {},
  isLoading: false,
  error: null,
};

const userReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN':
      return {
        ...state,
        isLoggedIn: true,
        userRole: action.userRole || null,
        username: action.username || null,
        userData: action.userData || {},
        isLoading: false,
        error: null,
      };
    case 'LOGOUT':
      return {
        ...state,
        isLoggedIn: false,
        userRole: null,
        username: null,
        userData: {},
        isLoading: false,
        error: null,
      };
    case 'SET_LOADING':
      return {
        ...state,
        isLoading: action.isLoading,
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.error,
        isLoading: false,
      };
    default:
      return state;
  }
};

export const UserProvider = ({ children }) => {
  const [state, dispatch] = useReducer(userReducer, initialState);

  useEffect(() => {
    // Check local storage for user data and token on component mount
    const storedUserData = localStorage.getItem('userData');
    const storedToken = localStorage.getItem('token');

    if (storedUserData && storedToken) {
        const decodedToken = jwtDecode(token);
        // Decode the token
      const { userRole, username } = decodedToken;

      // If user data and token are found, dispatch a login action
      dispatch({ type: 'LOGIN', userRole, username, userData: JSON.parse(storedUserData) });
    }
  }, []); // Empty dependency array ensures this effect runs only on mount

  const login = (userRole, username, userData, token) => {
    localStorage.setItem('userData', JSON.stringify(userData));
    localStorage.setItem('token', token);
    dispatch({ type: 'LOGIN', userRole, username, userData });
  };

  const logout = () => {
    dispatch({ type: 'LOGOUT' });
    localStorage.removeItem('userData'); // Remove entire user data
    localStorage.removeItem('token'); // Remove token
    window.location.href = '/user/login';
  };

  const setLoading = (isLoading) => {
    dispatch({ type: 'SET_LOADING', isLoading });
  };

  const setError = (error) => {
    dispatch({ type: 'SET_ERROR', error });
  };

  return (
    <UserContext.Provider value={{ state, login, logout, setLoading, setError }}>
      {children}
    </UserContext.Provider>
  );
};

export const useUser = () => {
  const context = useContext(UserContext);

  if (!context) {
    throw new Error('useUser must be used within a UserProvider');
  }

  return context;
};
