import React from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';

const BloodRequestForm = ({ authenticateToken }) => {
  const { register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = async (data) => {
    try {
      const response = await axios.post('http://localhost:5000/login/stf/request', data, {
        headers: {
          Authorization: `Bearer ${authenticateToken}`,
        },
      });

      console.log(response.data);
      // Handle success or redirect as needed
    } catch (error) {
      console.error('Error requesting blood:', error);
      // Handle error
    }
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="mb-4">
        <label htmlFor="bloodGroup" className="block text-sm font-medium text-gray-700">
          Blood Group:
        </label>
        <input
          type="text"
          id="bloodGroup"
          {...register('bloodGroup', { required: true })}
          className="mt-1 p-2 border rounded-md w-full"
        />
        {errors.bloodGroup && (
          <span className="text-red-500 text-sm">Blood group is required</span>
        )}
      </div>
  
      <div className="mb-4">
        <label htmlFor="unit" className="block text-sm font-medium text-gray-700">
          Unit:
        </label>
        <input
          type="number"
          id="unit"
          {...register('unit', { required: true, min: 1 })}
          className="mt-1 p-2 border rounded-md w-full"
        />
        {errors.unit && (
          <span className="text-red-500 text-sm">Please enter a valid unit value</span>
        )}
      </div>
  
      <div className="mb-4">
        <label htmlFor="patientName" className="block text-sm font-medium text-gray-700">
          Patient Name:
        </label>
        <input
          type="text"
          id="patientName"
          {...register('patientName', { required: true })}
          className="mt-1 p-2 border rounded-md w-full"
        />
        {errors.patientName && (
          <span className="text-red-500 text-sm">Patient name is required</span>
        )}
      </div>
  
      <div className="mb-4">
        <label htmlFor="patientAddress" className="block text-sm font-medium text-gray-700">
          Patient Address:
        </label>
        <textarea
          id="patientAddress"
          {...register('patientAddress', { required: true })}
          className="mt-1 p-2 border rounded-md w-full"
        />
        {errors.patientAddress && (
          <span className="text-red-500 text-sm">Patient address is required</span>
        )}
      </div>
  
      <div className="mb-4">
        <label htmlFor="patientContact" className="block text-sm font-medium text-gray-700">
          Patient Contact:
        </label>
        <input
          type="tel"
          id="patientContact"
          {...register('patientContact', { required: true, pattern: /^[0-9]+$/ })}
          className="mt-1 p-2 border rounded-md w-full"
        />
        {errors.patientContact && (
          <span className="text-red-500 text-sm">Please enter a valid contact number</span>
        )}
      </div>
  
      <div>
        <button
          type="submit"
          className="py-2 px-4 bg-red text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring focus:border-blue-300"
        >
          Request Blood
        </button>
      </div>
    </form>
  );
};

export default BloodRequestForm;
