import React, { useState } from 'react';
import axios from 'axios';
import { useUser } from './user'; // Import the UserProvider context

const BloodRequestForm = () => {
  const { state: { userRole }, logout } = useUser(); // Extract userRole from the UserProvider context
  const [bloodGroup, setBloodGroup] = useState('');
  const [unit, setUnit] = useState(0);
  const [patientName, setPatientName] = useState('');
  const [patientAddress, setPatientAddress] = useState('');
  const [patientContact, setPatientContact] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Check if user is authenticated and has the role of 'Recipient'
      if (userRole !== 'Recipient') {
        setErrorMessage('Permission denied. Only recipients can request blood.');
        return;
      }

      // Assuming you have stored the JWT token in localStorage after login
      const token = localStorage.getItem('token');
      
      const response = await axios.post(
        'http://localhost:5000/login/stf/request',
        {
          bloodGroup,
          unit,
          patientName,
          patientAddress,
          patientContact
        },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Handle successful response
      console.log(response.data);
    } catch (error) {
      // Handle errors
      console.error('Error requesting blood:', error);
      setErrorMessage(error.response.data.message);
      
      // If token is invalid or expired, log the user out
      if (error.response.status === 401) {
        logout();
      }
    }
  };

  return (
    <div>
      <h2>Blood Donation Request</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="bloodGroup">Blood Group:</label>
          <input type="text" id="bloodGroup" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)} />
        </div>
        <div>
          <label htmlFor="unit">Unit:</label>
          <input type="number" id="unit" value={unit} onChange={(e) => setUnit(e.target.value)} />
        </div>
        <div>
          <label htmlFor="patientName">Patient Name:</label>
          <input type="text" id="patientName" value={patientName} onChange={(e) => setPatientName(e.target.value)} />
        </div>
        <div>
          <label htmlFor="patientAddress">Patient Address:</label>
          <input type="text" id="patientAddress" value={patientAddress} onChange={(e) => setPatientAddress(e.target.value)} />
        </div>
        <div>
          <label htmlFor="patientContact">Patient Contact:</label>
          <input type="text" id="patientContact" value={patientContact} onChange={(e) => setPatientContact(e.target.value)} />
        </div>
        <button type="submit">Submit</button>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      </form>
    </div>
  );
};

export default BloodRequestForm;
