import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from "./StaffNavbar";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { MDBDataTable } from 'mdbreact'; // Import MDBDataTable

const DonorStockController = () => {
  const [donorStockList, setDonorStockList] = useState([]);
  const [editingDonorStock, setEditingDonorStock] = useState(null);

  useEffect(() => {
    getDonorStocks();
  }, []);

  const getDonorStocks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/login/stf/ds');
      setDonorStockList(response.data);
    } catch (error) {
      console.error('Error fetching donor stocks:', error);
    }
  };

  // Define columns for MDBDataTable
  const columns = [
    {
      label: 'Donor Name',
      field: 'donor_name',
      sort: 'asc',
      width: 150
    },
    {
      label: 'Blood Group',
      field: 'blood_group',
      sort: 'asc',
      width: 150
    },
    {
      label: 'Unit',
      field: 'unit',
      sort: 'asc',
      width: 150
    },
    {
      label: 'Age',
      field: 'age',
      sort: 'asc',
      width: 150
    },
    {
      label: 'Address',
      field: 'address',
      sort: 'asc',
      width: 150
    },
    {
      label: 'Donation Time',
      field: 'donation_time',
      sort: 'asc',
      width: 150
    },
    {
      label: 'Action',
      field: 'action',
      width: 100
    }
  ];

  // Map donor stock data to rows for MDBDataTable
  const rows = donorStockList.map((donorStock) => ({
    donor_name: (
      <input
        type="text"
        value={editingDonorStock?.donor_id === donorStock.donor_id ? editingDonorStock.donor_name : donorStock.donor_name}
        onChange={(e) => handleInputChange(e, donorStock.donor_id, 'donor_name')}
        readOnly={editingDonorStock?.donor_id !== donorStock.donor_id}
      />
    ),
    blood_group: (
      <input
        type="text"
        value={editingDonorStock?.donor_id === donorStock.donor_id ? editingDonorStock.blood_group : donorStock.blood_group}
        onChange={(e) => handleInputChange(e, donorStock.donor_id, 'blood_group')}
        readOnly={editingDonorStock?.donor_id !== donorStock.donor_id}
      />
    ),
    unit: (
      <input
        type="text"
        value={editingDonorStock?.donor_id === donorStock.donor_id ? editingDonorStock.unit : donorStock.unit}
        onChange={(e) => handleInputChange(e, donorStock.donor_id, 'unit')}
        readOnly={editingDonorStock?.donor_id !== donorStock.donor_id}
      />
    ),
    age: (
      <input
        type="text"
        value={editingDonorStock?.donor_id === donorStock.donor_id ? editingDonorStock.age : donorStock.age}
        onChange={(e) => handleInputChange(e, donorStock.donor_id, 'age')}
        readOnly={editingDonorStock?.donor_id !== donorStock.donor_id}
      />
    ),
    address: (
      <input
        type="text"
        value={editingDonorStock?.donor_id === donorStock.donor_id ? editingDonorStock.address : donorStock.address}
        onChange={(e) => handleInputChange(e, donorStock.donor_id, 'address')}
        readOnly={editingDonorStock?.donor_id !== donorStock.donor_id}
      />
    ),
    donation_time: (
      <input
        type="text"
        value={editingDonorStock?.donor_id === donorStock.donor_id ? editingDonorStock.donation_time : donorStock.donation_time}
        onChange={(e) => handleInputChange(e, donorStock.donor_id, 'donation_time')}
        readOnly={editingDonorStock?.donor_id !== donorStock.donor_id}
      />
    ),
    action: (
      <>
        {editingDonorStock?.donor_id === donorStock.donor_id ? (
          <>
            <button className="bg-green-500 text-white px-2 py-1 rounded mr-2" onClick={() => handleSaveUpdate(donorStock.donor_id)}>Save</button>
            <button className="bg-gray-500 text-white px-2 py-1 rounded" onClick={() => handleCancelUpdate()}>Cancel</button>
          </>
        ) : (
          <>
            <button className="bg-blue-500 text-white px-2 py-1 rounded mr-2" onClick={() => handleUpdateDonorStock(donorStock)}>Update</button>
            <button className="bg-red-500 text-white px-2 py-1 rounded" onClick={() => handleDeleteDonorStock(donorStock.donor_id)}>Delete</button>
          </>
        )}
      </>
    )
  }));

  const data = {
    columns,
    rows
  };

  const handleInputChange = (e, donorId, field) => {
    const { value } = e.target;
    setEditingDonorStock(prevState => ({
      ...prevState,
      donor_id: donorId,
      [field]: value
    }));
  };

  const handleUpdateDonorStock = (donorStock) => {
    setEditingDonorStock({ ...donorStock });
  };

  const handleCancelUpdate = () => {
    setEditingDonorStock(null);
  };

  const handleSaveUpdate = (donorId) => {
    axios.put(`http://localhost:5000/login/stf/ds/update/${donorId}`, editingDonorStock)
      .then((response) => {
        console.log('Donor stock updated successfully:', response.data);
        // Show success toast
        toast.success('Donor stock updated successfully');
        // Refresh the entire list from the server after update
        getDonorStocks();
        // Clear the editing donor
        setEditingDonorStock(null);
      })
      .catch((error) => {
        console.error('Error updating donor stock:', error);
        // Show error toast
        toast.error('Error updating donor stock');
      });
  };

  const handleDeleteDonorStock = (donor_id) => {
    axios.delete(`http://localhost:5000/login/stf/ds/delete/${donor_id}`)
      .then((response) => {
        console.log('Donor stock deleted successfully:', response.data);
        // Show success toast
        toast.success('Donor stock deleted successfully');
        // Refresh the entire list from the server after deletion
        getDonorStocks();
      })
      .catch((error) => {
        console.error('Error deleting donor stock:', error);
        // Show error toast
        toast.error('Error deleting donor stock');
      });
  };

  return (
    <>
      <ToastContainer />
      <Navbar />
      <div className="flex justify-between items-center px-4 py-2 ">
        <div className="hero-content bg-red max-w-2xl mx-auto">
          <h1 className="hero-title text-4xl md:text-5xl font-bold mb-4">Donor Management</h1>
          <p className="text-lg md:text-xl"><em>Empowering Lives, Saving Futures</em></p>
        </div>
      </div>
      <div className="container mx-auto p-4">
        <MDBDataTable
          striped
          bordered
          small
          data={data}
        />
      </div>
    </>
  );
};

export default DonorStockController;
