import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CampBloodDonationController = () => {
  const [donations, setDonations] = useState([]);
  const [editingDonationId, setEditingDonationId] = useState(null);

  useEffect(() => {
    getCampDonations();
  }, []);

  const getCampDonations = async () => {
    try {
      const response = await axios.get('http://localhost:5000/camp/donations');
      setDonations(response.data);
    } catch (error) {
      console.error('Error fetching camp donations:', error);
    }
  };

  const handleUpdateDonation = (donationId) => {
    setEditingDonationId(donationId);
  };

  const handleCancelUpdate = () => {
    setEditingDonationId(null);
  };

  const handleSaveUpdate = (donationToUpdate) => {
    axios
      .put(`http://localhost:5000/camp/donations/${donationToUpdate.id}`, donationToUpdate)
      .then((response) => {
        console.log('Blood donation updated successfully:', response.data);
        // Show success toast
        toast.success('Blood donation updated successfully');
        // Refresh the entire list from the server after update
        getCampDonations();
        // Clear the editing donation
        setEditingDonationId(null);
      })
      .catch((error) => {
        console.error('Error updating blood donation:', error);
        // Show error toast
        toast.error('Error updating blood donation');
      });
  };

  const handleDeleteDonation = (donationId) => {
    axios
      .delete(`http://localhost:5000/camp/donations/${donationId}`)
      .then((response) => {
        console.log('Blood donation deleted successfully:', response.data);
        // Show success toast
        toast.success('Blood donation deleted successfully');
        // Refresh the entire list from the server after deletion
        getCampDonations();
      })
      .catch((error) => {
        console.error('Error deleting blood donation:', error);
        // Show error toast
        toast.error('Error deleting blood donation');
      });
  };

  
  return (
    <>
      <ToastContainer />
      <div className="container mx-auto bg-violet-900 p-4">
        <table>
          <thead className="bg-white p-4">
            <tr className="bg-red">
              <th className="py-2 px-4 border">Donor Name</th>
              <th className="py-2 px-4 border">Blood Group</th>
              <th className="py-2 px-4 border">Donation Date</th>
              <th className="py-2 px-4 border">Donation Time</th>
              <th className="py-2 px-4 border">Blood Unit</th>
              {/* ... (other table headers) */}
              <th className="py-2 px-4 border">Action</th>
            </tr>
          </thead>
          <tbody>
  {donations.map((donation) => (
    <tr key={donation.id} className="hover:bg-gray-100">
      <td className="py-2 px-4 border">
        {editingDonationId === donation.id ? (
          <input
            type="text"
            value={donation.donorName}
            onChange={(e) => handleSaveUpdate({ ...donation, donorName: e.target.value })}
          />
        ) : (
          donation.donorName
        )}
      </td>
      <td className="py-2 px-4 border">
        {editingDonationId === donation.id ? (
          <input
            type="text"
            value={donation.bloodGroup}
            onChange={(e) => handleSaveUpdate({ ...donation, bloodGroup: e.target.value })}
          />
        ) : (
          donation.bloodGroup
        )}
      </td>
      <td className="py-2 px-4 border">
        {editingDonationId === donation.id ? (
          <input
            type="text"
            value={donation.donationDate}
            onChange={(e) => handleSaveUpdate({ ...donation, donationDate: e.target.value })}
          />
        ) : (
          donation.donationDate
        )}
      </td>
      <td className="py-2 px-4 border">
        {editingDonationId === donation.id ? (
          <input
            type="text"
            value={donation.donationTime}
            onChange={(e) => handleSaveUpdate({ ...donation, donationTime: e.target.value })}
          />
        ) : (
          donation.donationTime
        )}
      </td>
      <td className="py-2 px-4 border">
        {editingDonationId === donation.id ? (
          <input
            type="text"
            value={donation.bloodUnit}
            onChange={(e) => handleSaveUpdate({ ...donation, bloodUnit: e.target.value })}
          />
        ) : (
          donation.bloodUnit
        )}
      </td>
      {/* ... (other table cells) */}
      <td className="py-2 px-4 border">
        {editingDonationId === donation.id ? (
          <>
            <button
              className="bg-green-500 text-white px-2 py-1 rounded mr-2"
              onClick={() => handleSaveUpdate(donation)}
            >
              Save
            </button>
            <button
              className="bg-gray-500 text-white px-2 py-1 rounded"
              onClick={handleCancelUpdate}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <button
              className="bg-blue-500 text-white px-2 py-1 rounded mr-2"
              onClick={() => handleUpdateDonation(donation.id)}
            >
              Update
            </button>
            <button
              className="bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => handleDeleteDonation(donation.id)}
            >
              Delete
            </button>
          </>
        )}
      </td>
    </tr>
  ))}
</tbody>

        </table>
      </div>
    </>
  );
};

export default CampBloodDonationController;
